import osimport csvfrom statistics import mean# Variablespnl_csv = os.path.join('Resources', 'budget_data.csv')monthly_data = []           # monthly revenue datamonth_name = []             # month revenues realizedmonthly_change = []         # month-to-month changelines = []                  # stuff to print# grab data from CSV file using horrible I/O functionwith open(pnl_csv, 'r') as csvfile:        csvreader = csv.reader(csvfile, delimiter=',')        header = next(csvreader)                for row in csvreader:            month_name.append(str(row[0]))            monthly_data.append(float(row[1]))# create 'monthly_change' list total_months = len(monthly_data)second_month = 1for month in range(second_month,total_months):    monthly_change.append(monthly_data[month] - monthly_data[month-1])    # make month_name and monthly_change have same index month_name.pop(0)# calculate variables of interesttotal = "${:,.2f}".format(sum(monthly_data))average_change = "${:,.2f}".format(mean(monthly_change))greatest_increase = "${:,.2f}".format(max(monthly_change))gi_month = month_name[monthly_change.index(max(monthly_change))]greatest_decrease = "${:,.2f}".format(min(monthly_change))gd_month = month_name[monthly_change.index(min(monthly_change))]# print outputlines.append("Total Months: " + str(total_months))lines.append("Total: " + str(total))lines.append("Average Change: " + str(average_change))lines.append("Greatest Increase In Profits: " + str(gi_month) + "  " + str(greatest_increase))lines.append("Greatest Decrease In Profits: " + str(gd_month) + "  " + str(greatest_decrease))for line in lines:    print(line)outpath = os.path.join('Analysis', 'MyFile.txt')file_object = open(outpath, "a")for line in lines:    file_object.write(line)    file_object.write("\n")file_object.close()