import osimport csvfrom statistics import mean# Variablespnl_csv = os.path.join('Resources', 'budget_data.csv')monthly_data = []           # monthly revenue datamonth_name = []             # month revenues realizedmonthly_change = []         # month-to-month change# grab data from CSV file using horrible I/O functionwith open(pnl_csv, 'r') as csvfile:        csvreader = csv.reader(csvfile, delimiter=',')        header = next(csvreader)                for row in csvreader:            month_name.append(str(row[0]))            monthly_data.append(float(row[1]))# create 'monthly_change' list total_months = len(monthly_data)second_month = 1for month in range(second_month,total_months):    monthly_change.append(monthly_data[month] - monthly_data[month-1])    # make month_name and monthly_change have same index month_name.pop(0)# calculate variables of interesttotal = "${:,.2f}".format(sum(monthly_data))average_change = "${:,.2f}".format(mean(monthly_change))greatest_increase = "${:,.2f}".format(max(monthly_change))gi_month = month_name[monthly_change.index(max(monthly_change))]greatest_decrease = "${:,.2f}".format(min(monthly_change))gd_month = month_name[monthly_change.index(min(monthly_change))]line1 = "Total Months: " + str(total_months)line2 = "Total: " + str(total)line3 = "Average Change: " + str(average_change)line4 = "Greatest Increase In Profits: " + str(gi_month) + "  " + str(greatest_increase)line5 = "Greatest Decrease In Profits: " + str(gd_month) + "  " + str(greatest_decrease)print(line1)print(line2)print(line3)print(line4)print(line5)print(f"Total Months: {total_months}\n")print(f"Total: {total}\n")print(f"Average Change: {average_change}\n")print(f"Greatest Increase in Profits: {gi_month} {greatest_increase}\n")print(f"Greatest Decrease in Profits: {gd_month} {greatest_decrease}\n")